(() => {
    "use strict";
    let addWindowScrollEvent = false;
    function headerScroll() {
        addWindowScrollEvent = true;
        const header = document.querySelector(".header");
        const headerShow = header.hasAttribute("data-scroll-show");
        const startPoint = header.dataset.scroll ? header.dataset.scroll : 1;
        let scrollDirection = 0;
        document.addEventListener("windowScroll", (function(e) {
            const scrollTop = window.scrollY;
            if (scrollTop >= startPoint) {
                !header.classList.contains("_header-scroll") ? header.classList.add("_header-scroll") : null;
                if (headerShow) if (scrollTop > scrollDirection) header.classList.contains("_header-show") ? header.classList.remove("_header-show") : null; else !header.classList.contains("_header-show") ? header.classList.add("_header-show") : null;
            } else {
                header.classList.contains("_header-scroll") ? header.classList.remove("_header-scroll") : null;
                if (headerShow) header.classList.contains("_header-show") ? header.classList.remove("_header-show") : null;
            }
            scrollDirection = scrollTop <= 0 ? 0 : scrollTop;
        }));
    }
    setTimeout((() => {
        if (addWindowScrollEvent) {
            let windowScroll = new Event("windowScroll");
            window.addEventListener("scroll", (function(e) {
                document.dispatchEvent(windowScroll);
            }));
        }
    }), 0);
    headerScroll();
    if (document.querySelector("[data-search-block]")) {
        const container = document.querySelector("[data-search-block]"), input = container.querySelector("[data-search-input]"), dropdown = container.querySelector("[data-search-dropdown]"), title = container.querySelector("[data-search-title]"), link = container.querySelector("[data-search-link]"), items = dropdown.querySelectorAll("a"), listItems = dropdown.querySelectorAll("a [data-search-value]");
        const filterList = () => {
            let filter = input.value.toLowerCase().trim();
            title.style.display = "block";
            link.style.display = "flex";
            if (filter.length > 0 && filter != "") {
                title.style.display = "none";
                link.style.display = "none";
            }
            for (let i = 0; i < listItems.length; i++) {
                let txtVal = listItems[i].innerText.toLowerCase();
                let content = listItems[i].textContent;
                if (txtVal.indexOf(filter) > -1) {
                    const start = txtVal.indexOf(filter);
                    const end = start + filter.length;
                    listItems[i].innerHTML = content.slice(0, txtVal.indexOf(filter)) + `<span class="highlight">${content.slice(start, end)}</span>` + content.slice(end);
                    items[i].style.display = "flex";
                } else items[i].style.display = "none";
            }
        };
        document.addEventListener("click", (function(e) {
            if (!e.target.closest("[data-search-block]")) dropdown.classList.remove("search__block--active");
        }));
        items.forEach((item => {
            item.addEventListener("click", (e => {
                let val = e.target.closest("li").querySelector("span:first-child").innerHTML;
                input.value = val;
                dropdown.classList.contains("active") ? dropdown.classList.remove("active") : null;
            }));
        }));
        input.addEventListener("keyup", filterList);
    }
})();